import { createContext, useEffect, useState } from "react";
import {} from "../utils/firebase/firebase.utils";
import SHOP_DATA from "../data/shop-data.js";

import { getCategoriesAndDocuments } from "../utils/firebase/firebase.utils";

// the actual value you want to access
export const CategoriesContext = createContext({
	categories: null,
	set_categories: () => null,
	products: [],
	setProducts: () => null,
});

// provider is the actual component that gets wrapped around other components to give them acces to the context
export const CategoriesProvider = ({ children }) => {
	const [categories, set_categories] = useState({
		title: "Hats",
		items: [
			{
				id: 1,
				name: "Brown Brim",
				imageUrl: "https://i.ibb.co/ZYW3VTp/brown-brim.png",
				price: 25,
			},
			{
				id: 2,
				name: "Blue Beanie",
				imageUrl: "https://i.ibb.co/ypkgK0X/blue-beanie.png",
				price: 18,
			},
			{
				id: 3,
				name: "Brown Cowboy",
				imageUrl: "https://i.ibb.co/QdJwgmp/brown-cowboy.png",
				price: 35,
			},
			{
				id: 4,
				name: "Grey Brim",
				imageUrl: "https://i.ibb.co/RjBLWxB/grey-brim.png",
				price: 25,
			},
			{
				id: 5,
				name: "Green Beanie",
				imageUrl: "https://i.ibb.co/YTjW3vF/green-beanie.png",
				price: 18,
			},
			{
				id: 6,
				name: "Palm Tree Cap",
				imageUrl: "https://i.ibb.co/rKBDvJX/palm-tree-cap.png",
				price: 14,
			},
			{
				id: 7,
				name: "Red Beanie",
				imageUrl: "https://i.ibb.co/bLB646Z/red-beanie.png",
				price: 18,
			},
			{
				id: 8,
				name: "Wolf Cap",
				imageUrl: "https://i.ibb.co/1f2nWMM/wolf-cap.png",
				price: 14,
			},
			{
				id: 9,
				name: "Blue Snapback",
				imageUrl: "https://i.ibb.co/X2VJP2W/blue-snapback.png",
				price: 16,
			},
		],
	});
	const value = { categories, set_categories };

	useEffect(() => {
		const getCategoriesMap = async () => {
			const categoriesMap = await getCategoriesAndDocuments("categories");
			console.log(categoriesMap);
			set_categories(categoriesMap)
		};
		getCategoriesMap();
	}, []);

	return <CategoriesContext.Provider value={value}>{children}</CategoriesContext.Provider>;
};
