import "./checkout.route.scss";

import { useContext } from "react";
import { CartContext } from "../../contexts/cart.context";

function Checkout() {
	const { cartItems, cartCount, cartTotal, deleteFromCart } = useContext(CartContext);
	return (
		<div className="checkout-container container">
			<table class="table">
				<thead>
					<tr>
						<th scope="col">Product</th>
						<th scope="col"></th>
						<th scope="col">Quantity</th>
						<th scope="col">Price</th>
						<th scope="col">Remove</th>
					</tr>
				</thead>
				<tbody>
					{cartItems.map((item) => {
						return (
							<tr key={item.id}>
								<th>
									<img src={item.imageUrl} alt={item.name} />
								</th>
								<th>{item.name}</th>
								<td>{item.quantity}</td>
								<td>{item.price}</td>
								<td><span className="removeCartItem" onClick={() => deleteFromCart(cartItem)}>
								Remove
							</span></td>
							</tr>
						);
					})}
				</tbody>
			</table>
		</div>
	);
}

export default Checkout;
