import "./nav.scss";
import { ReactComponent as Logo } from "../../assets/crown.svg";
import { Link, Outlet } from "react-router-dom";
import { Fragment, useEffect, useState } from "react";

import { useContext } from "react";
import { UserContext } from "../../contexts/user.context";

function Nav({ links }) {
	const { currenntUser } = useContext(UserContext);
	const [thisUser, set_thisUser] = useState(null);

	useEffect(
		//useEffect is similar to componentDidMount in componant classes
		() => {
			console.log("nav:", currenntUser.displayName);
			if (currenntUser.displayName) {
				set_thisUser(currenntUser.displayName);
			}
		},
		[] //dependancies are state variables who's change causes the effect to be triggered
	);

	return (
		<Fragment>
			<div className="navigation">
				<Link className="logo-container" to="/">
					<Logo className="logo" />
				</Link>
				<div className="nav-links-container">
					{links.map(({ text, to }) => (
						<Link className="nav-link" to={to}>
							{text}
						</Link>
					))}
					<Link className="nav-link" to="#">
						{userName}
					</Link>
				</div>
			</div>
			<Outlet />
		</Fragment>
	);
}

export default Nav;
