import {} from "../utils/firebase/firebase.utils";
import SHOP_DATA from "../data/shop-data.js";

import { getCategoriesAndDocuments } from "../utils/firebase/firebase.utils";

// the actual value you want to access
export const ProductsContext = createContext({
	categories: null,
	set_categories: () => null,
	products: [],
	setProducts: () => null,
});

// provider is the actual component that gets wrapped around other components to give them acces to the context
export const ProductsProvider = ({ children }) => {
	const [categories, set_categories] = useState(getCategoriesAndDocuments("categories"));
	const value = { categories, set_categories };

	// useEffect(() => {
	// 	set_categories(getCategoriesAndDocuments("categories"));
	// }, []);

	return <ProductsContext.Provider value={value}>{children}</ProductsContext.Provider>;
};
