import styled from "styled-components";

export const cart_dropdown_container = styledComponents.div`
	position: absolute;
	width: 340px;
	height: 400px;
	display: flex;
	flex-direction: column;
	padding: 20px;
	border: 1px solid black;
	background-color: white;
	top: 55px;
	right: 6px;
	z-index: 5;
`;
export const empty_message = styledComponents.div`

		font-size: 18px;
		margin: 50px auto;
`;
export const cart_items = styledComponents.div`
		height: 280px;
		display: flex;
		flex-direction: column;
		overflow: scroll;
`;

export const cart_items_btn = styledComponents.div`
margin-top: auto;
`;
