import { createContext, useState /*, useEffect*/ } from "react";
import {} from "../utils/firebase/firebase.utils";

// the actual value you want to access
export const CartContext = createContext({
	isCartOpen: false,
	set_isCartOpen: () => null,
	cartItems: [],
	addItemToCart: () => {},
});

// provider is the actual component that gets wrapped around other components to give them acces to the context
export const CartProvider = ({ children }) => {
	const [isCartOpen, set_isCartOpen] = useState(true);
	const [cartItems, set_cartItems] = useState(null);
	// cart item structure
	/*
		{
			id: 1,
			name: "Brown Brim",
			imageUrl: "https://i.ibb.co/ZYW3VTp/brown-brim.png",
			price: 25,
			quantity: 10,
		}
	*/

	const addItemToCart = (itemToAdd) => {
		// Check if this item is already in 'cartItems'.
		const indexOfPresentItem = cartItems.findIndex((item) => {
			return itemToAdd.id === item.id;
		});
		// If item is already in 'cartItems', increase the quantity value, else add 'itemToAdd'.
		if (indexOfPresentItem !== -1) {
			cartItems[indexOfPresentItem].quantity += 1;
		} else {
			cartItems.push(itemToAdd);
		}

		// set_cartItems([...cartItems, item]);
	};

	const value = { isCartOpen, set_isCartOpen, cartItems, set_cartItems };

	// useEffect(() => {
	// 	// const unsubscribe = onAuthStateChangedListener(async (products) => {
	// 	// });
	// 	// return unsubscribe;
	// }, []);

	return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
};
